package com.example.dz2_month5.ui.onBoard.anapter

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.core.view.isVisible
import androidx.recyclerview.widget.RecyclerView.Adapter
import androidx.recyclerview.widget.RecyclerView.ViewHolder
import com.example.dz2_month5.databinding.ItemOnBoardingBinding
import com.example.dz2_month5.model.OnBoard
import com.example.dz2_month5.utils.loadImage

class OnBoardingAdapter(private val onStartClick: ()->Unit) : Adapter<OnBoardingAdapter.OnBoardingViewHolder>() {


    val data = arrayListOf<OnBoard>(
        OnBoard(
            "Have a good time",
            "you should",
            "need",
            "data:image/jpeg;base64,"
        ),
        OnBoard(
            "good time",
            "you should",
            "need",
            "data:image/jpeg;base64,"
        ),
        OnBoard("time", "you should", "need"),
    )

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OnBoardingViewHolder {
        return OnBoardingViewHolder(
            ItemOnBoardingBinding.inflate(
                LayoutInflater.from(parent.context),
                parent,
                false
            )
        )
    }

    override fun onBindViewHolder(holder: OnBoardingViewHolder, position: Int) {
        holder.bind(data.get(position))
    }

    override fun getItemCount() = data.size


    inner class OnBoardingViewHolder(private var binding: ItemOnBoardingBinding) :
        ViewHolder(binding.root) {
        fun bind(onBoard: OnBoard) {
            binding.tvHeader.text = onBoard.title
            binding.tvHeaderTwo.text = onBoard.titleOne
            binding.tvHeaderThree.text = onBoard.desc
            binding.ivBoard.loadImage(onBoard.image.toString())

            binding.btnStart.isVisible = adapterPosition == 2

            binding.btnStart.setOnClickListener {
                onStartClick()
            }

        }
    }
}